import React, { useEffect, useState } from 'react';
import fetchData from '../http-requests';

function RecipeList() {

  const [recipes, setRecipes] = useState(null);

  useEffect(() => {
    fetchData('pizza')
    .then(data => {
        setRecipes(data.hits);
    })
    .catch(err => console.log(err))
  }, [])

  return (
    <div className="App">
      <div className="col-lg-4 pb-1">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">{recipes.label}</h5>
              <p className="card-text">
                <strong>Calories: </strong>{recipes.calories}<br/>
                <strong>Dish type: </strong>{recipe.dishType}
                <strong>Ingredients: </strong>{recipe.ingredients.map(ingredient => ingredient.text)}
              </p>
            </div>
          </div>
      </div>
    </div>
  );
}

export default RecipeList;
