import React, { useState, useEffect } from 'react'
import UserDataService from '../services/user.js';

function CreateRecipe(props) {

  const initialRecipeList = {
    recipeArray: [],
    totalRecipes: 0
  }

  const [recipeList, setRecipeList] = useState(initialRecipeList);

  const getRecipeList = () => {
    UserDataService.getRecipes('beau')
    .then(res => {
      console.log(res.data)
      setRecipeList(res.data);
    })
    .catch(e => {
      console.error(e);
    });
  }

  useEffect(() => {
    getRecipeList();
  }, []);

  return (
    <div className="App">
      {recipeList && props.user && (
        <div>
          <h4>{`${props.user.name}'s Recipes`}</h4>
          <h5>Items in List: {recipeList.totalRecipes}</h5><br/>    
          <div className="container">
            <h5>Recipe List</h5>
            {recipeList.recipeArray.length > 0 ? (
              recipeList.recipeArray.map((item, i) => {
                return(
                  <div className="col-lg-4 pb-1">
                    <div className="card">
                      <img src={'https://www.industrialempathy.com/img/remote/ZiClJf-1920w.jpg'} className='card-img-top' alt={item.title}/>
                      <div className="card-body">
                        <h5 className="card-title">{item.title}</h5>
                        <p className="card-text">
                          Lorem ipsum
                        </p>
                      </div>
                    </div>
                  </div>
                )
              })
            ) : (
            <div className="col-sm-4">
              <p>No items in recipe list.</p>
            </div>
            )}
          </div>
        </div>
        )}
    </div>
  );
}

export default CreateRecipe;
