import fetchData from "../http-common";

const baseUrl = process.env.REACT_APP_URL;

 class DataServiceAppend {
    currentUrl = baseUrl;

    getFood(food) {
        this.currentUrl += `&q=${food}`;
        return fetchData(this.currentUrl);
    }

    getCuisine(cuisine) {
        this.currentUrl += `&cuisineType=${cuisine}`;
        return fetchData(this.currentUrl);
    }

    getDishType(dishType) {
        this.currentUrl += `&dishType=${dishType}`;
        return fetchData(this.currentUrl);
    }

    getCalories (calories) {
        this.currentUrl += `&calories=${calories}`;
        return fetchData(this.currentUrl);
    }
}
