import axios from 'axios';

const fetchData = async (query) => {
    const res = await axios.get('/posts');
    const posts = res.data;

    return (
        axios.get(`https://api.edamam.com/api/recipes/v2?type=public&app_id=${process.env.API_APP_ID}&app_key=${process.env.API_KEY}q=${query}`)
        .then(({data}) => {
            console.log(data);
            return JSON.stringify(data);
        })
        .catch((error) => {
            console.error(error);
        })
    )
}

export default fetchData;
