import axios from 'axios';

const fetchData = async (query) => {
    const res = await axios.get(`https://api.edamam.com/api/recipes/v2?type=public&app_id=${process.env.REACT_APP_API_ID}&app_key=${process.env.REACT_APP_API_KEY}&q=${query}`);
    const recipes = res.data;

    return recipes;
}

export default fetchData;
