import React, { useEffect, useState } from 'react';
import axios from 'axios';

function RecipeList() {

  const [recipes, setRecipes] = useState([]);
  const [searchFood, setSearchFood] = useState('');
  const [searchDishType, setSearchDishType] = useState('All Types');

  useEffect(() => {
    async function call () {
      const res = await axios.get(`https://api.edamam.com/api/recipes/v2?type=public&app_id=${process.env.REACT_APP_API_ID}&app_key=${process.env.REACT_APP_API_KEY}&q=pizza`);
      const recs = await res.data['hits'];
      const lst = recs.map(e => e['recipe']);
      console.log(JSON.stringify(lst.map(x=>x['label'])));
      setRecipes(lst);
    }
    call();
  }, []);
  console.log(recipes)
  return (
    <div className="App">
      <div className="row">
        {recipes.map((recipe) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <img src={recipe['image']} className='card-img-top' alt='Image of Food'/>
                <div className="card-body">
                  <h5 className="card-title">{recipe['label']}</h5>
                  <p className="card-text">
                    <strong>Calories: </strong>{Math.floor(recipe['calories'])}<br/>
                    <strong>Dish Type: </strong>{recipe['dishType']}<br/>
                    <strong>Ingredients: </strong><br/>
                    <ul>
                      {recipe['ingredientLines'].map((ingredient, i) => {
                        return (
                          <li key={i}>
                            {ingredient}
                          </li>
                        )
                      })}
                    </ul>
                  </p>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default RecipeList;
