import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import DataServiceAppend from '../services/recipes';
import { diet, health, cuisineType, mealType, dishType } from './options';

function RecipeList() {

  const [recipes, setRecipes] = useState([]);
  const [searchFood, setSearchFood] = useState('');
  const [cuisine, setCuisine] = useState('');
  const [mealType, setMealType] = useState('')
  const [searchDishType, setSearchDishType] = useState('All Types');

  // Add the search functions and the search button functionalities
  // Make sure the options are set for each autofill
  // Set a button on the individual recipes to go to a dedicated recipe page
  // Afterwards, limit the ingredients on display to 7, if more than 7, have the last one be ellipsis
  // Then, start on the cart, first making sure that only logged users can access their cart.


  const onChangeSearchFood = e => {
    const searchFood = e.target.value;
    setSearchFood(searchFood);
  }

  const onChangeCuisine = e => {
    const cuisine = e.target.value;
    setCuisine(cuisine);
  }

  const onChangeMealType = e => {
    const mealType = e.target.value;
    setMealType(mealType);
  }

  const onChangeDishType = e => {
    const dishType = e.target.value;
    setSearchDishType(dishType);
  }

  const onChangeDiet = e => {
    const diet = e.target.value;
    setDiet(diet);
  }


  return (
    <div className="App">
      <div className="row pb-1">
        <div className="col-lg-6">
          <div>Food</div>
          <TextField style={{marginTop: 10}} fullWidth="true" size="small" variant="outlined" placeholder="Search for food e.g. chicken" value={searchFood} onChange={onChangeSearchFood}/>
        </div>
        <div className="col-lg-6" >
          <div>Cuisine</div>
          <Autocomplete
          id="combo-box-demo"
          options={cuisineType}
          size="small"
          style={{ marginTop: 10}}
          renderInput={(params) => <TextField {...params} label="Type to search cuisines" variant="outlined" />}
          inputValue={cuisine}
          onInputChange={onChangeCuisine}
          />
        </div>
        <div className="col-lg-6">
          <div>Meal Type</div>
          <Autocomplete
          id="combo-box-demo"
          options={mealType}
          size="small"
          style={{ marginTop: 10}}
          renderInput={(params) => <TextField {...params} label="Type to search meal types" variant="outlined" />}
          inputValue={cuisine}
          onInputChange={onChangeCuisine}
          />
        </div>
        <div className="col-lg-6">
          <div>Dish Type</div>
          <Autocomplete
          id="combo-box-demo"
          options={dishType}
          size="small"
          style={{ marginTop: 10 }}
          renderInput={(params) => <TextField {...params} label="Type to search dish types" variant="outlined" />}
          inputValue={cuisine}
          onInputChange={onChangeCuisine}
          />
        </div>
        <div className="col-lg-6">
          <div>Diet Type</div>
          <Autocomplete
          id="combo-box-demo"
          options={diet.map(type => {
            type = type.replace('-', ' ');
            return type.charAt(0).toUpperCase() + type.slice(1);
          })}
          size="small"
          style={{ marginTop: 10}}
          renderInput={(params) => <TextField {...params} label="Type to search diet types" variant="outlined" />}
          inputValue={cuisine}
          onInputChange={onChangeCuisine}
          />
        </div>
        <div className="col-lg-6">
          <div>Health Labels</div>
          <Autocomplete
          id="combo-box-demo"
          options={mealType}
          size="small"
          style={{ marginTop: 10}}
          renderInput={(params) => <TextField {...params} label="Type to search health labels" variant="outlined" />}
          inputValue={cuisine}
          onInputChange={onChangeCuisine}
          />
        </div>  
        <div className="d-grid gap-2" style={{marginTop: 20}}>
          <button className="btn btn-success btn-lg" type="button">Search</button>
        </div>     
      </div>
      <div className="row">
        {recipes.map((recipe) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <img src={recipe['image']} className='card-img-top' alt={`Image of ${recipe['label']}`}/>
                <div className="card-body">
                  <h5 className="card-title">{recipe['label']}</h5>
                  <p className="card-text">
                    <strong>Source: </strong>{recipe['source']}<br/>
                    <strong>Calories: </strong>{Math.floor(recipe['calories'])}<br/>
                    <strong>Dish Type: </strong>{recipe['dishType']}<br/>
                    <strong>Ingredients: </strong><br/>
                    <ul>
                      {recipe['ingredientLines'].map((ingredient, i) => {
                        return (
                          <li key={i}>
                            {ingredient}
                          </li>
                        )
                      })}
                    </ul>
                  </p>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default RecipeList;
