import React, { useState } from 'react';

import DataServiceAppend from '../services/recipes';

function RecipeList() {

  const [recipes, setRecipes] = useState([]);
  const [searchFood, setSearchFood] = useState('');
  const [cuisine, setCuisine] = useState(['All Cuisines']);
  const [searchDishType, setSearchDishType] = useState('All Types');

  // useEffect(() => {
  //   async function call () {
  //     const recs = await fetchData('rice');
  //     const lst = recs.map(e => e['recipe']);
  //     setRecipes(lst);
  //   }
  //   call();
  // }, []); 

  const onChangeSearchFood = e => {
    const searchFood = e.target.value;
    setSearchFood(searchFood);
  }

  const findByFood = async () => {
    const data = await DataServiceAppend.getFood(searchFood);
    const list = data.map(food => food['recipe']);
    setRecipes(list);
  }


  return (
    <div className="App">
      <div className="row pb-1">
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            value={searchFood}
            onChange={onChangeSearchFood}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByFood}
            >
              Search
            </button>
          </div>
        </div>
        <Autocomplete
        id="combo-box-demo"
        options={['option1', 'option2']}
        style={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Combo box" variant="outlined" />}
        />
        <div className="input-group-append">
          <button
            className="btn btn-outline-secondary"
            type="button"
            onClick={findByFood}
          >
            Search
          </button>
        </div>
      </div>
      <div className="row">
        {recipes.map((recipe) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <img src={recipe['image']} className='card-img-top' alt={`Image of ${recipe['label']}`}/>
                <div className="card-body">
                  <h5 className="card-title">{recipe['label']}</h5>
                  <p className="card-text">
                    <strong>Source: </strong>{recipe['source']}<br/>
                    <strong>Calories: </strong>{Math.floor(recipe['calories'])}<br/>
                    <strong>Dish Type: </strong>{recipe['dishType']}<br/>
                    <strong>Ingredients: </strong><br/>
                    <ul>
                      {recipe['ingredientLines'].map((ingredient, i) => {
                        return (
                          <li key={i}>
                            {ingredient}
                          </li>
                        )
                      })}
                    </ul>
                  </p>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default RecipeList;
