import React, { useState, useEffect } from 'react'
import UserDataService from '../services/user.js';

function CreateRecipe(props) {

  const initialRecipeList = {
    recipeArray: [],
    totalRecipes: 0
  }

  const [recipeList, setRecipeList] = useState(initialRecipeList);

  const getRecipeList = () => {
    UserDataService.getRecipes('beau')
    .then(res => {
      console.log(res.data)
      setRecipeList(res.data);
    })
    .catch(e => {
      console.error(e);
    });
  }

  useEffect(() => {
    getRecipeList();
  }, []);

  return (
    <div className="App">
      {recipeList && props.user && (
        <div>
          <h4>{`${props.user.name}'s Recipes`}</h4>
          <h5>Items in List: {recipeList.totalRecipes}</h5><br/>    
          <div className="container">
            {recipeList.recipeArray.length > 0 ? (
              recipeList.recipeArray.map((item, i) => {
                return(
                  <div className="row" key={i}>
                    <div className="col">
                      {item.date.substring(0,10)}
                    </div>
                    <div className="col">
                      {item.item}
                    </div>
                    <div className="col d-flex justify-content-between">
                      <button onClick={() => handleDecrease(i)} style={{marginLeft: 40}}>-</button>
                      {item.quantity}
                      <button onClick={() => handleIncrease(i)} style={{marginRight: 40}}>+</button>
                    </div>
                    <div className="col">
                      <button onClick={() => handleDelete(i)}>Remove Item</button>
                    </div>
                  </div>
                )
              })
            ) : (
            <div className="col-sm-4">
              <p>No items in recipe list.</p>
            </div>
            )}
          </div>
        </div>
        )}
    </div>
  );
}

export default CreateRecipe;
