import React, { useEffect, useState } from 'react';
import UserDataService from '../services/user';

function Cart(props) {

  const initialCartState = {
    cartArray: [],
    itemsInCart: 0
  }

  const [cart, setCart] = useState(initialCartState);


  const getCart = () => {
    console.log(props.user)
    UserDataService.getCart('beau')
    .then(res => {
      console.log(res.data)
      setCart(res.data);
    })
    .catch(e => {
      console.error(e);
    });
  }

  const handleDecrease = (i) => { 
    if (cart.cartArray[0].quantity > 0) {
      const clone = {...cart};
      clone.cartArray[0].quantity--;
      setCart(clone);
    }
  }

  const handleIncrease = (i) => {
    const clone = {...cart};
    clone.cartArray[0].quantity++;
    setCart(clone)
  }

  useEffect(() => {
    getCart();
  }, []);

  return (
    <div className="App">
      {cart && props.user && (
        <div>
          <h4>{`${props.user.name}'s Cart`}</h4>
          <h5>Items in Cart: {cart.itemsInCart}</h5>
          <div className="container">
            {cart.cartArray.length > 0 ? (
              cart.cartArray.map((item, i) => {
                return(
                  <div className="row">
                    <div className="col">
                      {item.date.substring(0,10)}
                    </div>
                    <div className="col">
                      {item.text}
                    </div>
                    <div className="col">
                      <button onClick={(i) => handleIncrease(i)}>+</button>
                      {item.quantity}
                      <button onClick={(i) => handleDecrease(i)}>-</button>
                    </div>
                  </div>
                )
              })
            ) : (
            <div className="col-sm-4">
              <p>No items in cart.</p>
            </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
