import React, { useEffect, useState } from 'react';
import fetchData from '../http-common';

function RecipeList() {

  const [recipes, setRecipes] = useState([]);
  const [searchFood, setSearchFood] = useState('');
  const [searchDishType, setSearchDishType] = useState('All Types');

  useEffect(() => {
    async function call () {
      const recs = await fetchData('rice');
      const lst = recs.map(e => e['recipe']);
      setRecipes(lst);
    }
    call();
  }, []);

  const onChangeSearchFood = e => {
    const searchFood = e.target.value;
    setSearchFood(searchFood);
  }

  return (
    <div className="App">
      <div className="row pb-1">
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            value={searchFood}
            onChange={onch}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByName}
            >
              Search
            </button>
          </div>
        </div>
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by zip"
            value={searchZip}
            onChange={onChangeSearchZip}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByZip}
            >
              Search
            </button>
          </div>
        </div>
        <div class="dropdown">
          <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
            Cuisines
          </button>
          <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Another action</a></li>
            <li><a class="dropdown-item" href="#">Something else here</a></li>
          </ul>
        </div>
      </div>
      <div className="row">
        {recipes.map((recipe) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <img src={recipe['image']} className='card-img-top' alt='Image of Food'/>
                <div className="card-body">
                  <h5 className="card-title">{recipe['label']}</h5>
                  <p className="card-text">
                    <strong>Source: </strong>{recipe['source']}<br/>
                    <strong>Calories: </strong>{Math.floor(recipe['calories'])}<br/>
                    <strong>Dish Type: </strong>{recipe['dishType']}<br/>
                    <strong>Ingredients: </strong><br/>
                    <ul>
                      {recipe['ingredientLines'].map((ingredient, i) => {
                        return (
                          <li key={i}>
                            {ingredient}
                          </li>
                        )
                      })}
                    </ul>
                  </p>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default RecipeList;
